import React, { useEffect, useState } from "react";
import "../component/index.css";

const Logo = () => {
  const [progress, setProgress] = useState(100); // Начинаем с 100%

  useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prevProgress) => Math.max(prevProgress - 1, 0)); // Уменьшаем ширину
    }, 50); // Скорость анимации

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <div className="containerLogo">
      <svg
        width="541"
        height="207"
        viewBox="0 0 541 207"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        style={{ overflow: "visible" }}
      >
        {/* Ваш SVG-код */}
        <rect
          width={progress} // Ширина SVG зависит от прогресса
          height="207"
          fill="blue" // Цвет для примера
        />
      </svg>

      <div className="text" style={{ opacity: progress / 100 }}>
        Ваш текст здесь
      </div>
    </div>
  );
};

export default Logo;
