import React, { useEffect, useState } from 'react';
import "../component/index.css";

const Logo = () => {
    const [displayedText, setDisplayedText] = useState('');
    const fullText = 'Code life';
    
    useEffect(() => {
        let index = 0;
        const interval = setInterval(() => {
            if (index < fullText.length - 1) { // Изменено на fullText.length
                console.log(fullText[index]);
                setDisplayedText((prev) => prev + fullText[index]);
                index++;
            } else {
                clearInterval(interval);
            }
        }, 200); // Задержка в 200 мс между символами

        return () => clearInterval(interval); // Очистка интервала при размонтировании
    }, []);

    return (
        <div className="containerLogo">
            <h1>
                {displayedText}
                {displayedText.length < fullText.length && <span className="cursor">|</span>} {/* Курсор */}
            </h1>
        </div>
    );
};

export default Logo;
