import React, { useEffect, useRef, useState } from "react";
import "../component/index.css";

const Logo = () => {
  const [progress, setProgress] = useState(0);
  const totalWidth = 541; // Ширина вашего SVG

  useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prevProgress) => (prevProgress + 1) % 100);
    }, 50); // Скорость анимации

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <div className="containerLogo">
      <svg
        width="541"
        height="207"
        viewBox="0 0 541 207"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        style={{ overflow: "visible" }}
      >
        <defs>
          <mask id="mask">
            <rect x="0" y="0" width="541" height="207" fill="white" />
            <rect
              x={(progress / 100) * totalWidth} // Позиция маски
              y="0"
              width={totalWidth}
              height="207"
              fill="black"
            />
          </mask>
        </defs>
        <g mask="url(#mask)">{/* Вставьте здесь ваш SVG-код */}</g>
      </svg>

      <div className="text" style={{ opacity: progress / 100 }}>
        Ваш текст здесь
      </div>
    </div>
  );
};

export default Logo;
