import React, { useEffect, useRef, useState } from "react";
import "../component/index.css";

const Logo = () => {
  const [progress, setProgress] = useState(0);
  const cursorRef = useRef(null);
  const totalWidth = 541; // Ширина вашего SVG

  useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prevProgress) => (prevProgress + 1) % 100);
    }, 50); // Скорость анимации

    return () => {
      clearInterval(timer);
    };
  }, []);

  useEffect(() => {
    if (cursorRef.current) {
      const cursorWidth = 12; // Ширина курсора
      const cursorPosition = (progress / 100) * (totalWidth - cursorWidth);
      cursorRef.current.style.transform = `translateX(${cursorPosition}px)`;
    }
  }, [progress]);

  return (
    <div className="containerLogo">
      <span className="CodeLife">
        <svg
          width="541"
          height="207"
          viewBox="0 0 541 207"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          {/* Ваш SVG код */}
        </svg>
      </span>

      <span className="cursor" ref={cursorRef}>
        <svg
          width="12"
          height="208"
          viewBox="0 0 12 208"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path d="M0 0H12V208H0V0Z" fill="white" />
        </svg>
      </span>
    </div>
  );
};

export default Logo;
