import React, { useEffect, useState } from 'react';
import "../component/index.css";

const Logo = () => {
    const texts = ['Code life', 'life Code']; // Массив текстов для отображения
    const [displayedText, setDisplayedText] = useState('');
    const [currentTextIndex, setCurrentTextIndex] = useState(0);
    const [isWriting, setIsWriting] = useState(true); // Состояние для отслеживания, пишем ли мы текст
    const [isUnderlined, setIsUnderlined] = useState(false); // Состояние для подчеркивания текста

    useEffect(() => {
        let index = 0; // Индекс для текущего текста
        const fullText = texts[currentTextIndex]; // Текущий текст для отображения

        const interval = setInterval(() => {

            if (index < fullText.length + 1) {
                setDisplayedText(fullText.slice(0, index++)); // Обновление отображаемого текста
            } else {
                setIsWriting(false); // При достижении конца текста, переключаемся на чтение
                setTimeout(() => {
                    setIsWriting(true); // Возвращаемся к писанию
                    setCurrentTextIndex((currentTextIndex + 1) % texts.length); // Переходим к следующему тексту
                    setDisplayedText(''); // Очищаем текущее отображение
                    setIsUnderlined(false); // Снимаем подчеркивание
                }, 3000); // Задержка в 3 секундах перед переходом к следующему тексту
            }

        }, 200); // Задержка в 200 мс между символами

        return () => clearInterval(interval); // Очистка интервала при размонтировании
    }, [isWriting, currentTextIndex]); // Зависимости для эффекта

    return (
        <div className="containerLogo">
            <h1 style={{ backgroundColor: isUnderlined ? 'underline' : 'none', backgroundColor: isUnderlined ? 'red' : '#121214' }}>
                {displayedText}
                {isWriting && <span className="cursor">|</span>} {/* Курсор */}
            </h1>
        </div>
    );
};

export default Logo;
